import { useRef } from "react"

export function Login({onLogin}) {

  // создаем рефы
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = (e) => {
    e.preventDefault();

    onLogin({
      email: emailRef.current.value,
      password: passwordRef.current.value
    });
}

    return(
        <section className="sign">
          <h1 className="sign__title">Вход</h1>
          <form className="sign__form">
            <input className="sign__input" ref={emailRef} required type="email" name="signInEmail" placeholder="Email"></input>
            <input className="sign__input" ref={passwordRef} required type="text" name="signInPassword" placeholder="Пароль"></input>
            <button className="sign__button" onSubmit={handleSubmit} type="submit">Войти</button>
          </form>
        </section>
    )
}
