import React from "react";
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import { Card } from "./Card.js";

export function Main({cards, onEditAvatar, onCardLike, onCardDelete, selectedCard, onEditProfile, onAddPlace}) {

  // подписка на контекст с текущим пользователем 
  
  const currentUser = React.useContext(CurrentUserContext);

  const sectionWithCards = () => {
    if (cards.length > 0) {
      return cards.map((cardInfo) => (
         <Card key={cardInfo._id} card={cardInfo} onCardLike={onCardLike} onCardDelete={onCardDelete} selectedCard={selectedCard} />
      ));
    }
  };

  // разметка
    return(
        <main>
        <section className="profile">
          <a className="profile__avatar-hover" href="#" onClick={onEditAvatar}><img className="profile__avatar" src={currentUser.avatar} alt="Аватар профиля"/></a>
          <div className="profile__info">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button className="profile__edit-button" type="button" onClick={onEditProfile}></button>
            <p className="profile__status">{currentUser.about}</p>
          </div>
          <button className="profile__add-button" type="button" onClick={onAddPlace}></button>
        </section>
        <section className="elements">
          <ul className="elements__list">
            {sectionWithCards()}
          </ul>
        </section>
        </main>
    );
};
